  #create EM using given parameters
  #######################################
	em_command <- paste('enrichmentmap build analysisType="generic"',
			'gmtFile=', gmtFile,
			'pvalue=', 1,
			'qvalue=', 1,
			'similaritycutoff=',0.05,
			'coefficients=',"JACCARD")
